import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import "../styles/CreateEventModal.css"

const CreateEventModal = ({visiable, onClose, profileInfo, accessToken}) => {
    const [needHelp, setNeedHelp] = useState(false);
    const [cities, setCities] = useState([]);

    async function getAllCity() {
        try {
            const response = await axios.get("http://localhost:8000/organizations/all_city");
            setCities(response.data);
        }
        catch(error) {        
            alert("server error");
        }
    }

    useEffect(() => {
        getAllCity();
    }, []);

    if (!visiable) return null;
    return(
        <div className="edit-profile-modal-overlay" onClick={onClose}>
            <div 
                className="edit-profile-modal-content" 
                onClick={(e) => e.stopPropagation()} 
            >
                <button 
                    className="close-button" 
                    onClick={onClose}
                >
                    &times;
                </button>
                <h2>Создание события</h2>
                <form className="row g-3">
                    <div className="col-12">
                        <div className="form-check">
                            <input 
                                className="form-check-input" 
                                type="checkbox" 
                                id="gridCheck"
                                value={needHelp}
                                onChange={() => setNeedHelp(!needHelp)}
                            />
                            <label className="form-check-label" htmlFor="gridCheck">
                                Просьба о помощи
                            </label>
                        </div>
                    </div>
                    <div className="col-12">
                        <label htmlFor="inputAbout" className="form-label">Короткий текст (виден в списке событий)</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="inputAbout"
                            value={null}   
                            onChange={null} 
                        />
                    </div>
                    <div className="col-12">
                        <label htmlFor="inputAbout" className="form-label">Полный текст (виден на странице события)</label>
                        <input 
                            type="text" 
                            className="form-control" 
                            id="inputAbout"
                            value={null}   
                            onChange={null} 
                        />
                    </div>
                    <div className="col-md-6">
                        <label htmlFor="inputState" className="form-label">Город</label>
                        <select 
                            id="inputState" 
                            className="form-select"
                            value={null}
                            onChange={null}
                        >
                        {cities.map((city) => (
                            <option key={city.id} value={city.city}>{city.city}</option>
                        ))}
                        </select>
                    </div>
                    <div className="col-6">
                        { needHelp 
                            ?
                        <label htmlFor="inputAbout" className="form-label">Необходимо человек</label>
                            :
                        <label htmlFor="inputAbout" className="form-label">Имеется человек</label>
                        }
                        <input 
                            type="text" 
                            className="form-control" 
                            id="inputAbout"
                            value={null}   
                            onChange={null} 
                        />
                    </div>
                    <div className="col-12">
                        <button 
                            type="submit" 
                            className="btn btn-primary"
                            onClick={null}
                        >
                            Отправить
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default CreateEventModal;
