import OrganizationList from "../components/OrganizationList";
import axios from "axios"
import { useEffect } from "react";
import { useFetching } from "../hooks/useFetching";
import { useState } from "react";
import Spiner from "../components/Spiner";
import "../styles/Organizations.css"

const Organizations = () => {
    const [organizations, setOrganizations] = useState([]);
    const [fetchOrganizations, isLoading, error] = useFetching( async () => {
        const response = await getOrganization();
        setOrganizations(response.data);
    })

    async function getOrganization() {
        try {
            const response = await axios.get("http://localhost:8000/organizations/find_organization_or_person");
            console.log(response);
            return response;

        }
        catch(error) {
            console.log(error);
        }

    }

    useEffect( () => {
        fetchOrganizations(); // eslint-disable-next-line
      }, [])

    const title = "123"

    return (
        <div className="organizationsPage">
            {error
                ? <h1 className="errorEvents">Ошибка {error | errorFilter}</h1> :
            <OrganizationList organizations={organizations} title="Список организаций и пользователей"/>
            }
        </div>
    );
}

export default Organizations;
