import "../styles/GiveHelp.css"
import EventList from "../components/EventList";
import { useState } from "react";
import { useEffect } from "react";
import axios from "axios";
import { useFetching } from "../hooks/useFetching";


const GiveHelp = () => {
    const [events, setEvents] = useState([]);
    const [fetchEvents, isLoading, error] = useFetching( async () => {
        const response = await getEvents();
        setEvents(response.data);
    })

    async function getEvents() {
        const response = await axios.get("http://localhost:8000/events/get_all_event?need_help=true");
        return response;
    }

    useEffect( () => {
        fetchEvents();
      }, [])

    return (
        <div className="giveHelpPage">
            <div className="giveHelpInfoPage">
               <EventList events={events} title='Список событий:'/>
            </div>
        </div>
    );
};

export default GiveHelp;
