import "../styles/EventPage.css"
import { useParams } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
import { useFetching } from "../hooks/useFetching";
import axios from "axios";

const PageHelp = () => {
    const params = useParams();
    const [event, setEvent] = useState({});
    const [fetchEventById, isLoading, error] = useFetching( async () => {
        const response = await getEventById(params.id);
        setEvent(response.data);
        console.log(response.data);
    })

    async function getEventById(id) {
        const response = await axios.get(`http://localhost:8000/events/get_event_by_id?id_event=${id}`);
        return response;       
    } 


    useEffect(() => {
        fetchEventById();
    }, [])

    return(
        <div className="eventPage">
            {isLoading
                ?
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                </div>   
                :
                <div className="container text-center">
                    <div className="row justify-content-center">
                        <div className="col-4 text-bg-secondary">
                            <img src={profileInfo.photo_url}/>
                        </div>
                        <div className="col-8 text-bg-secondary">

                        </div>
                    </div>
                </div>
            }
        </div>
    );
}

export default PageHelp;
