import {Link} from "react-router-dom";
import {useState} from "react";
import { useEffect } from "react";
import axios from "axios";
import Cookies from 'js-cookie';

const Navbar = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [accessToken, setAccessToken] = useState('')

    // function getMe(access_token) {   
    //     const response_me = axios.post(`http://localhost:8000/organizations/me?access_token=${access_token}`)
    //     // setIsLogin(true);
    //     console.log(response_me);
    // }

    function logout() {
        Cookies.remove("support_access_token");
        setIsLogin(false);
    }

    async function checkMe () {
        await setAccessToken(Cookies.get("support_access_token"));
        if (accessToken) {
            setIsLogin(true);
        }
    }

    function goProfile() {

    }

    useEffect(() => {
        checkMe();
    }, [])

    return (
        <nav className="navbar navbar-expand-lg bg-body-tertiary">
            <div className="container-fluid">
                <Link to='/' className="navbar-brand">
                    Помоги.рф
                </Link>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                    <div className="navbar-nav">
                        <Link to='/about' className="nav-link">
                            О нас
                        </Link>
                    </div>
                    <div className="navbar-nav">
                        <Link to='/contacts' className="nav-link">
                            Контакты
                        </Link>
                    </div>
                    {isLogin 
                        ?
                    <>
                    <div className="navbar-nav">
                        <Link to='/profile/1' className="nav-link" onClick={goProfile}>
                            Профиль
                        </Link>
                    </div>
                    <div className="navbar-nav">
                        <Link to='#' className="nav-link" onClick={logout}>
                            Выйти
                        </Link>
                    </div>
                    </>
                        :
                    <div className="navbar-nav">
                        <Link to='/login' className="nav-link">
                            Войти
                        </Link>
                    </div>
                    }
                </div>    
            </div>
        </nav>
    );
};

export default Navbar;
