import EventList from "./EventList";
import { useState } from "react";
import axios from "axios";
import { useFetching } from "../hooks/useFetching";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import CommentItem from "./CommentItem";


const AdditionalProfileInformation = ({eventButtonVisible, setEventButtonVisible}) => {
    const params = useParams();
    const [events, setEvents] = useState([]);
    const [comments, setComments] = useState([]);
    const [fetchProfileEvents, isEventsLoading, eventsEror] = useFetching( async () => { // ДОделать загрузку евентов
        const response = await getProfileEvents();
        setEvents(response.data);
    })
    const [fetchProfileComments, isCommentsLoading, commentsError] = useFetching( async () => {
        const response = await getProfileComments();
        setComments(response.data);
    })

    async function getProfileEvents() {
        const response = await axios.get(`http://localhost:8000/events/get_event_by_id_organization?id_organization=${params.id}`);
        return response;
    }

    async function getProfileComments() {
        const response = await axios.get(`http://localhost:8000/comments/get_comments_by_id_organization?id_organization=${params.id}`);
        return response;
    }

    useEffect( () => {
        fetchProfileEvents(); 
      }, [eventButtonVisible])

    return(
        <div className="card">
            <div className="card-header">
                <ul className="nav nav-tabs card-header-tabs">
                    { eventButtonVisible
                        ?
                    <>
                    <li className="nav-item">
                        <a 
                            className="nav-link active" 
                            aria-current="true" 
                            href="#"
                            onClick={() => setEventButtonVisible(true)}
                        >
                            События
                        </a>
                    </li>
                    <li className="nav-item">
                        <a 
                            className="nav-link" 
                            href="#"
                            onClick={() => setEventButtonVisible(false)}
                        >
                            Комментарии
                        </a>
                    </li>
                    </>
                        :
                    <>
                    <li className="nav-item">
                        <a 
                            className="nav-link" 
                            aria-current="true" 
                            href="#"
                            onClick={() => setEventButtonVisible(true)}
                        >
                            События
                        </a>
                    </li>
                    <li className="nav-item">
                        <a 
                            className="nav-link active" 
                            href="#"
                            onClick={() => setEventButtonVisible(false)}
                        >
                            Комментарии
                        </a>
                    </li>
                    </>
                    }
                </ul>
            </div>
            { eventButtonVisible
                ?
            <div className="card-body">
                <EventList events={events} title="Список событий этого пользователя:"/>
            </div>
                :
            <div className="card-body">
                <CommentItem/>
            </div>
            }
        </div>
    );
}

export default AdditionalProfileInformation;
