import EventList from "./EventList";
import { useState } from "react";
import axios from "axios";
import { useFetching } from "../hooks/useFetching";
import { useEffect } from "react";

const AdditionalProfileInformation = ({eventButtonVisible, setEventButtonVisible}) => {
    const params = useState();
    const [events, setEvents] = useState([]);
    const [fetchProfileEvents, isLoading, error] = useFetching( async () => {
        //const response = await getEvents(filter);
        //setEvents(response.data);
    })

    async function getProfileEvents() {
        const response = await axios.get(`http://localhost:8000/events/get_all_event?need_help=`);
        return response;
    }

    useEffect( () => {
        fetchProfileEvents();
        console.log(params.id);  
      }, [])

    return(
        <div className="card text-center">
            <div className="card-header">
                <ul className="nav nav-tabs card-header-tabs">
                    { eventButtonVisible
                        ?
                    <>
                    <li className="nav-item">
                        <a 
                            className="nav-link active" 
                            aria-current="true" 
                            href="#"
                            onClick={() => setEventButtonVisible(true)}
                        >
                            События
                        </a>
                    </li>
                    <li className="nav-item">
                        <a 
                            className="nav-link" 
                            href="#"
                            onClick={() => setEventButtonVisible(false)}
                        >
                            Комментарии
                        </a>
                    </li>
                    </>
                        :
                    <>
                    <li className="nav-item">
                        <a 
                            className="nav-link" 
                            aria-current="true" 
                            href="#"
                            onClick={() => setEventButtonVisible(true)}
                        >
                            События
                        </a>
                    </li>
                    <li className="nav-item">
                        <a 
                            className="nav-link active" 
                            href="#"
                            onClick={() => setEventButtonVisible(false)}
                        >
                            Комментарии
                        </a>
                    </li>
                    </>
                    }
                </ul>
            </div>
            <div className="card-body">
                <h5 className="card-title">Special title treatment</h5>
                <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
                <a href="#" className="btn btn-primary">Go somewhere</a>
            </div>
        </div>
    );
}

export default AdditionalProfileInformation;
